var routes = (function() {
	var $body = $('body');
	var section = $('#main .inner');

	var bodyClassChange = function(page) {
		$body.removeClass(function(index, css){
			return (css.match (/(^|\s)page-\S+/g) || []).join(' ');
		});
		$body.addClass('page-'+page);
	}
	

	var changePage = function(page, mode, alterHistory) {
		bodyClassChange(page);

		if (alterHistory === undefined) {
			alterHistory = true;
		}

		// if on initial page load, load content instantly
		if (mode == "init") {
			$body.hide();
			section.load('src/pages/' + page + '.html', function() {
				$body.fadeIn(1000);	
				if (alterHistory) {
					history.pushState(page, null, page);	
				}
			});
			
		} 
		else {
			section.fadeOut(200, function() {
				setTimeout(function() {
					section.load('src/pages/' + page + '.html', function() {
						section.fadeIn(350);
						if (alterHistory) {
							history.pushState(page, null, page);	
						}
					})	
				}, 300)
			});
		}
	}

	// when users go back and forth
	window.addEventListener('popstate', function(e) {
		changePage(e.state, "normal", false)
		bodyClassChange(e.state);
		navigationUI.findActiveNav(e.state)
	})


	// load page to correct routes on initial load
	var initialRoute = (function() {
		if (!sessionStorage.redirect) {
			changePage('home', "init");
		} else {
			let url = sessionStorage.redirect;
			var page = url.slice(url.lastIndexOf('/') + 1, url.length);
			alert('changing to page: ' + page);
			changePage(page, "init");
		}
	})();

	// when users load friendly url, got o that url
	// var hash = window.location.hash;

	// if (hash === "#/" || hash.length === 0) {
	// 	changePage('home', "init");	
	// } else {
	// 	hash = hash.replace("#/", "");
	// 	changePage(hash, "init");
	// }

	return {
		changePage: changePage
	}

})();
var navigation = (function() {
	var $navListDropdown = $('nav#menu li.dropdown');
	var $navListUL = $('.dropdown ul')
	var $navListAnchor = $('nav#menu a');
	var $body = $('body');
		
	$navListAnchor.on("click", function(e) {
		// stop default beahvior
		e.stopPropagation();

		// make active nav
		var $section_name = $(this).attr('class');
		$navListAnchor.removeClass("active");
		$(this).addClass('active');

		$("a.close").trigger("click");
		// change page
		routes.changePage($section_name);
		e.preventDefault();

	})

	$('a.logo').on("click", function(e) {
		e.preventDefault();
		routes.changePage('home')
	});

})(routes);
var background = (function() {
	var findNumOfImages = function() {
		return new Promise(function(resolve, reject) {
			var count = 0;

			$.ajax({
				url: 'src/images/',
				success: function(data) {
					$(data).find("li > a").each(function() {
						var pathname = $(this).context.pathname;

						if (pathname.indexOf("_bg") > -1) {
							count ++;
						}
					})
					resolve(count);
				}
			});
		}); 		
	}
	
	var getRandomInt = function(min, max) {
	    return Math.floor(Math.random() * (max - min + 1)) + min;
	}

	var changeBackground = function(className, imagePreface) {
		var $sectionToChange = $('.' + className);
		findNumOfImages().then(function(data) {

			var backgroundNo = getRandomInt(1, data);
			var background = imagePreface + '-' + backgroundNo;
			
			$sectionToChange.css({
				'background': "linear-gradient( rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), url(src/images/" + background + '.jpg)',
				'background-size' : 'cover'
			})
			
		})
	}

	 $('.flexslider').flexslider({
	 	controlNav: false,
	 	directionNav: false,
	 	randomize: true
	 });



	// changeBackground('page-bg', 'home_bg');
})();
